args:
@media (max-width: 768px) {
    .class-schedule-container {
        padding: 0 10px;
    }

    .class-schedule-title {
        font-size: 24px;
        margin-top: 10px;
    }

    .class-schedule-subtitle {
        margin-bottom: 20px;
    }

    .class-schedule-filter-card {
        padding: 15px;
        margin-bottom: 15px;
    }

    .class-schedule-filter-title {
        font-size: 1.2rem;
        margin-bottom: 10px;
    }
}

@media (max-width: 768px) {
    .card-view-grid {
        grid-template-columns: 1fr;
        gap: 10px;
        padding: 0;
    }

    .card-view-card {
        padding: 15px;
        border-radius: 12px;
    }

    .card-view-title {
        font-size: 1rem;
    }

    .card-view-id {
        font-size: 0.75rem;
    }

    .card-view-info {
        font-size: 0.85rem;
    }
}

/* Animation for calendar sliding */
.calendar-slide-wrapper {
    animation-duration: 0.3s;
    animation-fill-mode: both;
    animation-timing-function: ease-out;
}

.calendar-slide-wrapper.slide-next {
    animation-name: slideInRight;
}

.calendar-slide-wrapper.slide-prev {
    animation-name: slideInLeft;
}

@keyframes slideInRight {
    from {
        transform: translateX(50px);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

@keyframes slideInLeft {
    from {
        transform: translateX(-50px);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}
